INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (28,28,'jpilla','<l>Anno ab incarnatione D<ex>omi</ex>ni M° CC° III°. Eu, Uc, </l><l>coms de Rodes, <ex>et</ex> eu, <ex>Guillem</ex>, coms de Rodes, sos</l><l>fills, p<ex>er</ex> nos e per totz los n<ex>ost</ex>res, p<ex>er</ex></l><l>bona fe e ses engan, <ex>et</ex> ab aquesta carta, p<ex>er</ex></l><l>amor de Deu e de ma do<ex>n</ex>na s<ex>an</ex>c<ex>t</ex>a Maria e</l><l>dellas armas de n<ex>ost</ex>res paires e de n<ex>ost</ex>ras maires e de</l><l>tot n<ex>ost</ex>re linnatgue, donam e lauda<ex>m</ex> e liura<ex>m</ex> <ex>et</ex> ab titol p<ex>er</ex>feig de do</l><l>nazo p<ex>er</ex> totz temps autorga<ex>m</ex>,</l><l>so es saubut las maisos del</l><l>borc de S<ex>anct</ex>i Amanz q<ex>ue</ex> foro d’en Guill<ex>em</ex> Echor e</l><l>de Feiriol so fill, ab las cortz <ex>et</ex> ab l’ort</l><l><ex>et</ex> ab los intrars <ex>et</ex> ab los issirs <ex>et</ex> ab totz los</l><l>p<ex>er</ex>tenementz que p<ex>er</ex>tener i devo, a Deu <ex>et</ex> a</l><l>s<ex>an</ex>c<ex>t</ex>a Maria d’a Bonacumba <ex>et</ex> a.n Bertran l’abat <ex>et</ex> als</l><l>fraires que ara i so ni p<ex>er</ex>adena<ex>n</ex>t i serau</l><l>p<ex>er</ex> totz temps ; e desvestem ne nos e</l><l>veste<ex>m</ex> ne la maiso. <ex>Et</ex> aquestas maisos</l><l>sobredichas aiatz e tengatz francament e quitiament e</l><l>soltas de comus e de gachas e de totz usatgues.</l><lb/><l><ex>Et</ex> aquest dos fo faigz ella ma d’en Ugo de Panat que</l><l>era cellarers de Bonacu<ex>m</ex>ba.</l><lb/><l>S<ex>ignum</ex> Bec della Barreira, e W.</l><l>della Barreira sos botz, B<ex>er</ex>nart Gausb<ex>er</ex>t, W.</l><l>B<ex>er</ex>inguer de Cambolaz, B<ex>er</ex>nart B<ex>er</ex>inguer de Panat,</l><l>D. Mala Mosca, Espero, R. Cordura, P. Gros,</l><l>Corbeira. D. Socada, Ponz de Pebrac e fraire Isarnz.</l><lb/><l>Tot aquest dos sobredigz, eu Uc, coms de</l><l>Rodes <ex>et</ex> eu Guillem, sos fills, coms de Rodes,</l><l>volem q<ex>ue</ex> sia tengutz p<ex>er</ex> totz temps p<ex>er</ex> nos e p<ex>er</ex></l><l>totz n<ex>ost</ex>res successors e p<ex>er</ex> zo que sia plus</l><l>ferm, avem i faigz pausar nostres sagels.</l><l>S<ex>ignum</ex> R. Bornadels</l><l>que aquesta carta fez.</l>');
INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (3,3,'jpilla','<l>[... placue]rant Israheli et</l><l>universo Benjamin.</l><l>Venitque ad David in Hebron cum</l><l>viginti viris, et fecit David Abner et</l><l>viris ejus qui venerant cum eo con-</l><l>-vivium. Et dixit Abner ad David :</l><l>"Surgam et congregem ad te dominum</l><l>meum regem omnem Israhel et ineam</l><l>tecum foedus et imperes omnibus</l><l>sicut desiderat anima tua". Quum</l><l>ergo deduxisset David Abner et ille</l><l>isset in pace, statim pueri David et</l><l>Joab venerunt, cesis latronibus cum</l><l>praeda magna nimis. Abner autem</l><l>non erat cum David in Hebron, quia</l><l>jam dimiserat eum et profectus</l><l>fuerat in pace, et Joab et omnis exerci-</l><l>-tus qui erat cum eo postea venerant.</l><l>Nuntiatum est itaque Joab a nar-</l><l>rantibus : "Venit Abner filius Ner</l><l>ad regem et dimisit eum et abiit in pa-</l><l>ce". Et ingressus est Joab ad regem et</l><l>ait : "Quid fecisti ? Ecce venit Abner ad</l><l>te. Quare dimisisti eum et abiit et</l><l>recessit ? Ignoras Abner filius Ner quo-</l><l>niam ad hoc venit ut deciperet te</l><l>et sciret exitum tuum et introitum tu-</l><l>um et nosset omnia quae agis ?"</l><l>Egressus itaque Joab a David, misit nunti-</l><l>os post Abner [et] reduxit eum a cisterna</l><l>Syra, ignorante David. Quumque</l><l>redisset Abner in Hebron, seorsum</l><l>abduxit eum Joab ad medium portae</l><l>ut loqueretur ei in dolo. Et percussit</l><l>eum ibi in inguine et mortuus est, in</l><l>ultionem sanguinis Asahel fratris ejus.</l><l>Quod quum audisset David rem</l><l>jam gestam, ait : "Mundus ego sum et</l><l>regnum meum apud Dominum usque in sem-</l><l>piternum a sanguine Abner filii Ner,</l><l>et veniat super caput Joab et super</l><l>omnem domum patris ejus, nec</l><l>deficiat de domo Joab fluxum seminis</l><l>sustinens et leprosus [et] tenens fusum</l><l>et cadens gladio et indige\n/s pane !"</l><l>Igitur Joab et Abisai frater ejus</l><l>interfecerunt Abner eo quod occidis-</l><l>set Asahel fratrem eorum in Gabaon</l><l>in proelio. Dixit autem David ad Jo-</l><l>ab et ad omnem populum qui erat</l><l>cum eo : "Scindite vestimenta vestra</l><l>et accingimini saccis et plangite</l><l>ante exsequias Abner !" Porro rex</l><l>David sequebatur feretrum.</l><l>Quumque sepelissent Abner in He-</l><l>bron, levavit rex vocem suam et</l><l>flevit super tumulum Abner ; fle-</l><l>vit autem et omnis populus.</l><l>Plangensque rex et lugens Abner, ait :</l><l>"Nequaquam ut mori solent ignavi</l><l>mortuus est Abner : manus tuæ</l><l>non sunt ligatae et pedes tui non</l><l>sunt compedibus adgravati, sed</l><l>sicut solent cadere coram filiis</l><l>iniquitatis corruisti." Congeminans-</l><l>que omnis populus flevit super</l><l>eum. Quumque venisset universa</l><l>multitudo cibum capere cum David,</l><l>clara adhuc die, juravit David</l><l>dicens : "Haec faciat mihi Deus et haec</l><l>addat, si ante occasum solis gusta-</l><l>vero panem vel aliud quicquam."</l><l>Omnisque populus audivit et pla-</l><l>cuerunt eis cuncta quae fecit rex</l><l>in conspectu totius populi, et cogno-</l><l>vit omne vulgus et universus</l><l>Israhel in die illa quoniam non actum</l><l>fuisset a rege ut occideretur Abner</l><l>filius Ner. Dixit quoque rex ad</l><l>servos suos : "Num ignoratis quoniam</l><l>princeps et maximus cecidit ho-</l><l>die in Israhel ? Ego autem adhuc</l><l>delicatus, et unctus rex ; porro viri isti</l><l>filii Sarviae duri mihi sunt. Nunc</l><l>retribuat Dominus facienti malum jux-</l><l>ta malitiam suam !"</l>');
INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (30,30,'jpilla','<l>La vida</l><lb/><l>Perdigons fon joglars e saup trobar e viular trop ben ;</l><l>e fon de l’evescat de Gavaudan d’un castel q<ex>e</ex> a nom</l><l>Lesperon</l><l>e fon fills d’un paubre homen</l><l>qe era pescaire ;</l><l>e p<ex>er</ex> son sen e p<ex>er</ex> son trobar</l><l>poiet en gran pretz <ex>et</ex> en</l><l>gran honor,</l><l>tant qe.l dalfins d’Alvernge lo tenc per son cavallier</l><l>e.l vestic e l’armet ab se lonc temps</l><l>e.il donet terra e renda.</l><l>E tuich li baron e.il prince li fasiant trop grand honor.</l><l>Lonc temps ac de grans bonas aventuras</l><l>mas mout li camieron pois las bonas aventuras e vengron li</l><l>las malas</l><l>qez el p<ex>er</ex>det los amics e las amigas e.l pretz e l’onor e</l><l>l’aver</l><l>e pois el se rendet en l’orden de Cistel e lai moric.</l><l>Et aqui son escriutas de las soas canssos.</l><lb/><l>La canso</l><lb/><l>Tot l’an mi ten Amors d’aital faiso</l><l>cum estai cel q’a.l mal dond s’adormis</l><l>e morria dormen, tant es conquis,</l><l>en breu d’ora, entro c’om lo ressida ;</l><l>atressi m’es tals dolors demedida</l><l>qe.m don’Amors que sol no.m sai ni.m sen,</l><l>e cuich morir ab aqest marrimen,</l><l>tro qe m’esfortz de far una chansso</l><l>qe.m resside d’aqest tormen on so.</l><l>Ben fetz Amors l’usatge del larro</l><l>qand encontra cellui d’estraing pays</l><l>e.il fai creire c’aillors es sos camis,</l><l>tro qu’el li ditz : « Bels amics, tu mi guida »</l><l>et enaissi es mainta gens trahida</l><l>qe lai.l mena on puois lo lia e.l pren ;</l><l>et eu puosc dir atressi veramen</l><l>qez eu segei Amor tant co.il saup bo,</l><l>tant mi menet tro m’ac en sa preiso.</l><lb/><lb/><l>E te.m lai pres on non trob rezensso</l><l>mas de ma mort, c’aissi lor abellis</l><l>entre midonz et Amors cui sui fis :</l><l>lor platz ma mortz e lor es abellida,</l><l>mas eu sui cel que merce no lor crida</l><l>plus cum fai cel q’es liuratz a tormen</l><l>que sap que plus no.il valria nien</l><l>clamar merce, aia tort o razo :</l><l>per q’eu m’en lais qe mot non lor en so.</l><lb/><l>Pero non sai cal me fassa o cal no,</l><l>pois per mon dan m’engana e.m trahis,</l><l>Amors vas cui estau totz temps aclis</l><l>al sieu plazer, c’aitals fo m’escarida ;</l><l>e tengr’o trop a paraula grazida</l><l>sol no.m mostres tant brau chaptenemen ;</l><l>mas sill s’aunis pe.l mieu dechazemen,</l><l>ben fai semblan qe m’aia cor fello,</l><l>qe per mon dan non tem far mespreiso.</l><lb/><l>E fatz esfortz s’ab ira joi mi do</l><l>mas en aisso.m conort e m’afortis</l><l>contra.l desir en c’Amors m’a assis ;</l><l>aissi cum cel c’a bataill’aramida,</l><l>qe sap de plan sa razos es delida</l><l>qand es en cort on hom dreich no.ill consen,</l><l>et ab tot so si.s combat eissamen</l><l>mi combat eu en cort on no.m ten pro</l><lb/><l>qe Amors m’a forjutgat non sai co.</l><lb/><l>Ai ! Bel Esper, pros dompna issernida,</l><l>tant grans dreitz es si d’Amor mal mi pren</l><l>car anc de vos mi parti, las, dolen,</l><l>per cel’una qe ja no.m tenra pro,</l><l>anz m’aucira en sa doussa preiso.</l>');
INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (31,31,'jpilla','<l>Loys, ainsné filz du roy de France, daulphin de Viennoys, conte de Valentinoys et de Dioys,</l><l>a n<ex>ost</ex>re amé <ex>et</ex> feal Girault Aymar,</l><l>escuier, seigneur de Granhan, salut et dilection.</l><l>Comme par l’accort et traictié fait entre nous, d’une part, et vous, d’aut<ex>re</ex>,</l><l>sur le fait de l’eschange de la place, terre et seigneurie de Grillen a la terre</l><l><ex>et</ex> seigneurie de Marsenne <ex>et</ex> la moitié du</l><l>peage de Laine et des empses de Savasse vous nous doiez</l><l>p<ex>resen</ex>tement delivrer et mettre en noz mains ladicte place,</l><l>terre <ex>et</ex> seigneurie de Grillen ensemble</l><l>toutes les rentes et reven<ex>ue</ex>s, ainsi que par le traictié sur ce fait peut app<ex>ar</ex>oir,</l><l>savoir vous faisons que, nous confians a plain de la loyauté,</l><l>souffisance <ex>et</ex> bonne preudomie de n<ex>ost</ex>re amé <ex>et</ex> feal</l><l>escuier d’escuierie Hugues de Verdun,</l><l>seigneur de S<ex>ain</ex>te Camelle, icelui avons co<ex>m</ex>mis et ordonné, co<ex>m</ex>mettons</l><l>et ordonnons par ces p<ex>resen</ex>tes a prandre la possession,</l><l>la garde et joïssance de lad<ex>icte</ex> place de Grillen, pour et</l><l>en n<ex>ost</ex>re nom. Si vous mandons et expressement enjoingnons</l><l>que icelle place de Grillen avec toute la reven<ex>ue</ex>,</l><l>ainsi que ledit traictié le contient, vous lui baillez et</l><l>mettez en ses mains pour et en n<ex>ost</ex>re nom. Et en ce faisant</l><l>nous vous en tendrons quitte et deschargé partout ou besoing sera,</l><l>sans aucunem<ex>en</ex>t all<ex>e</ex>r au contraire. Car ainsi</l><l>nous plaist il et voulons estre fait.</l><l>Donné au Monteil Aymart, le XXVIme jour de may, l’an de grace</l><l>mil CCCC quarante <ex>et</ex> sept.</l><l>Par monseigneur le daulphin</l><l>en son conseil</l><l>[signé :] Bochetel [paraphe]</l>');
