INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (39,39,'jpilla','<l>Nous, officiaulz de la court monsignour le</l><l>princier grant archidiacre en l’eglize de Verdun, faisons</l><l>cougnissant a tous que en la presence de Arnouth dit</l><l>Vion, clerc, notaire <ex>et</ex> tabellion publique de l’auctoritei</l><l>de l Saint Empire de Roume, jurei <ex>et</ex> fiauble de ladite</l><l>court, louqueil nous creons en cest fait <ex>et</ex> en plus grant <ex>et</ex></l><l>liqueis porte n<ex>ost</ex>re pooir en ceste p<ex>ar</ex>tie, reprint Jehans</l><l>des Hermoizes, escuiiers, filz signour Robert des</l><l>Hermoizes, ch<ex>evalie</ex>r, de reverent peire en n<ex>ost</ex>re</l><l>Signour Henri, p<ex>ar</ex> la grace de Deu evesque de Verdun,</l><l>teil droit <ex>et</ex> teile p<ex>ar</ex>tie com il avoit, pooit <ex>et</ex> devoit avoir</l><l>en dix reires de bleif que li hoir signour Jehant le</l><l>Cornu, ch<ex>evalie</ex>r, ont einz rente <ex>et</ex> einz croueies de</l><l>Gourai<ex>n</ex>court ;</l><l>et celui fiez remint lidis Jehans en la main ledit reverent</l><l>peire Henri pour do<ex>n</ex>neir a Henri de Bryey, fil signour</l><l>Jake de Briey, ch<ex>evalie</ex>r, qui fut ; liqueis Henris,</l><l>escuiers, en reprint doudit reverent peire Henri evesque</l><l>en teil servize com lidis fiez devoit. Se fut fait en la</l><l>presence de signour Hue de Gibercourt, ch<ex>evalie</ex>r,</l><l>Otignon de Manhuere et Husson de Blanzy, homes de</l><l>l’eveschié. Liqueis sires Hues, ch<ex>evalie</ex>rs, ai muis son</l><l>sael en ces p<ex>re</ex>sentes letres avoc le n<ex>ostr</ex>e sael en</l><l>tesmo<ex>n</ex>gnage dez chozes dessusdites. Lezqueiles letres</l><l>furent faites l’an de grace n<ex>ost</ex>re Signour mil trois cens</l><l>dix <ex>et</ex> sept ans, le samedi aprés feste saint Nicholay en</l><l>yver. (Signe) Ar. Vionni.</l>');
