INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (57,57,'jpilla','<l>E dis : « Ja per Dieu no<hi rend="sup">.</hi>l valra</l><l>Qe ja tan luein no fogira</l><l>ni no<hi rend="sup">.</hi>s rescondra tan prion,</l><l>ans ne qeria tot lo mon</l><l>tan ca<ex>n</ex> n’es de mar ni de t<ex>er</ex>ra,</l><l>q’eu no<hi rend="sup">.</hi>l trobe, se no.s sot<ex>er</ex>ra. »</l><l>E part se d’aqui ab aitan</l><l>e es venguts esp[er]onan</l><l>ves un cami gran e ferat :</l><l>un esclau frecs el a trobat.</l><l>« Aisi, dis el, a mo vegaire,</l><l>es caval passat non a gaire. »</l><l>E dis qe per aqel tenria</l><l>aitan can l’esclau trobaria.</l><l>E met s’el cami d’ambladura</l><l>e anet tan ca<ex>n</ex> lo jorn dura,</l><l>qe vila ni castel no vi,</l><l>ni anc, si tot s’en escursi,</l><l>no<hi rend="sup">.</hi>s laiset per aco d’anar,</l><l>car ades se cuja tardar.</l><l>E cant ac un petit anat,</l><l>el ausi denan se levat</l><l>un gra<ex>n</ex> brugtz de cavaliers,</l><l>qe ferres ni futz ni asiers</l><l>resono e meno bruidors.</l><l>Jaufres ailai s’en vai de cors,</l><l>tan can poc, de gran esperos,</l><l>e escrida : « Qui es, baros,</l><l>qe d’aital ora<hi rend="sup">.</hi>us combates ?</l><l>Pos vezer no<hi rend="sup">.</hi>us puesc, repondes ! »</l><l>E el pres enan ad anar</l><l>car ades se cuja tardar,</l><l>aisi con om volontairos</l><l>qe non es de re temeros.</l><l>Ab aitan la bataila fali</l><l>e Jaufre, can re non ausi,</l><l>Escouta es si estanchatz ;</l><l>e es se mot meravilatz</l><l>un sun ni vas cal part tenio</l><l>cels qe tan fort si combatio,</l><l>qe negun nu au ni num ve.</l><l>Ab tan el garda denan se</l><l>e ausi plainer e roflar</l><l>un cavalier e sospirar</l><l>e fo nafratz mot malamen.</l><l>E Jaufre venc vas lui corren</l><l>e troba<hi rend="sup">.</hi>l jasen, estendut</l><l>el sol e ac gran sanc perdut</l><l>qe la paraula l’es falida.</l><l>E Jaufre aitan can pot crida :</l><l>« Cavalier, dis el, respon me</l><l>[e diga