INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (10,10,'jpilla','In nomine s<ex>an</ex>c<ex>t</ex>e et [i]ndividue Trinitatis, amen. Ph<ex>ilippus</ex> D<ex>e</ex>i gr<ex>ati</ex>a Franc<ex>orum</ex> rex. Noverint universi presentes parit<ex>er</ex> <ex>et</ex> futuri quod Gilo de Flagiaco tenebat quodda<ex>m</ex> feodum de nobis, scil<ex>ice</ex>t avoeriam de Emant <ex>et</ex> lignariu<ex>m</ex>. De lignario aut<ex>em</ex> erat dissensio int<ex>er</ex> Gilonem <ex>et</ex> alios advocatos qui dicebant se de jure partem hab<ex>er</ex>e in lignario illo si manentes essent in Must<ex>er</ex>iolo. Inquisita aut<ex>em</ex> <ex>et</ex> cognita veritate hujus rei, didicimus quod nichil hab<ex>er</ex>ent de jure illi alii advocati in pred<ex>i</ex>c<ex>t</ex>o lignario. Illud aut<ex>em</ex> totum q<ex>uo</ex>d prefatus Gilo habebat in avoeria <ex>et</ex> lignario tam in feodo q<ex>u</ex>am in d<ex>omi</ex>nio vendidit Roberto abb<ex>at</ex>i <ex>et</ex> conventui S<ex>an</ex>c<ex>t</ex>i Germani de Pratis. Quitavit etiam eis quicq<ex>u</ex>id reclamabat in uxore Herois <ex>et</ex> sororibus ejusdem uxoris de Taste- dos. Nos aut<ex>em</ex> ad petitionem pred<ex>i</ex>c<ex>t</ex>i Gilonis hanc venditione<ex>m</ex> <ex>con</ex>firmamus <ex>et</ex>, ut p<ex>er</ex>petuu<ex>m</ex> robur obtineat, sigilli n<ex>ost</ex>ri munimine <ex>et</ex> regii no<ex>min</ex>is karact<ex>er</ex>e inferius annotato presente<ex>m</ex> paginam roboramus. Actum Parisius, anno ab incarnatione D<ex>omi</ex>ni M° CC° s<ex>e</ex>c<ex>un</ex>do, regni n<ex>ost</ex>ri anno vicesimo  quarto. Astantibus in palatio n<ex>ost</ex>ro quoru<ex>m</ex> no<ex>m</ex>i<ex>n</ex>a supposita sunt <ex>et</ex> signa. Dapifero n<ex>u</ex>llo. Signum Guidonis buticularii. S. Mathei  camerarii. S. Droconis constabularii. <br/> Data vacante cancellaria p<ex>er</ex> manum f<ex>rat</ex>ris Garini. <br/> [Monogramme] PHILIPPUS.');
INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (13,13,'jpilla','Vez ci l’estat de la ville de Noion. La ville doit XVI<hi rend="sup">M</hi> livres poi plus ou mains, a l’estimation du maire <ex>et</ex> des eschevins, c’est a savoir a ceus qui sunt asenez des rentes a vie <ex>et</ex> a ceus qui lour ont baillez lour quemandes en garde <ex>et</ex> a ceus desquels il ont emprunté p<ex>our</ex> le besoing de la ville, don nos, auditors, devons avoir les nons <ex>et</ex> les parties, que nos vos porteron. Vez ci la delivrance ordrenee p<ex>ar</ex> devant nos, auditors, du conseil du maior <ex>et</ex> des jurés de Noion au plus profitablement que l’e<ex>n</ex> pourra p<ex>our</ex> la ville \aquiter/ si plaist a nostre sei<ex>n</ex>gnor le roy P<ex>re</ex>mierement il doivent XLII<hi rend="sup">C</hi> livres <ex>et</ex> XX l. paris<ex>is</ex> de rente a vie chascun an, p<ex>our</ex> laquele deite rendre chascun an la ville, du quemun assenteme<ex>n</ex>t, s’est acordee que il paieront par taille ceste p<ex>re</ex>miere anee VI<hi rend="sup">M</hi> livres <ex>et</ex> chascu<ex>n</ex> an ap<ex>ré</ex>s cele meesme su<ex>m</ex>me, se celui qui i sera de p<ex>ar</ex> lou roy voit que il le puissent fere, jusqu’a tant que la deite devant dite (soit), c’est a savoir XVI<hi rend="sup">M</hi> livres \<ex>et</ex> les rentes a vie/, soient paiees, <ex>et</ex> sera levee la taille p<ex>ar</ex> (cil) \celui/ que li roys i establira, <ex>et</ex> en contera p<ex>ar</ex> devant les mestres des contes le roy, presenz aucu<ex>n</ex>s des p<ex>ro</ex>udeshomes de Noion qui pour ce seront apelez. <ex>Et</ex> pour ce que la ville sera trop grevee de si grant su<ex>m</ex>me paier, il requierent nostre seignor le roy qu’i lour face rabatre les usures <ex>et</ex> que ceus qui lour voudront quit<ex>er</ex> aucunes [des deites ou des rentes] a vie, qui lour soient rabatues de lour so<ex>m</ex>me, pour ce que il ont eu plus que chatel et il semble a nos, auditors, que i