INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (34,34,'jpilla','Anno D<ex>omi</ex>ni M° CCC vicesimo quinto et vicesima sexta die octobris, d<ex>omi</ex>no Karolo Francor<ex>um</ex> et Navarre rege regnante, ego Johannes Saureri <ex>de</ex> Marologio co<ex>n</ex>fiteor et recognosco vob<ex>is</ex>, disc<ex>re</ex>to viro Guirardo Petri, thesaurario regio in sen<ex>escalli</ex>a Bell<ex>icadr</ex>i et Nem<ex>ausi</ex>, q<ex>uod</ex> vos d<ex>e</ex> hoc in q<ex>u</ex>o vob<ex>is</ex> tenebar rat<ex>io</ex>ne pedagii d<ex>e</ex> Larsis defalcastis m<ex>ih</ex>i dece<ex>m</ex> l<ex>i</ex>b<ex>ra</ex>s decem sol<ex>idos</ex> turon<ex>ensium</ex> p<ex>ro</ex> parte regia, quas solvi mandato judicis co<ex>mmun</ex>is cur<ex>ie</ex> Gaballitani et loc<ex>um</ex> ten<ex>entis</ex> baylivii d<ex>i</ex>c<ex>t</ex>e co<ex>mmun</ex>is cur<ex>ie</ex> rat<ex>io</ex>ne septuaginta sol<ex>idorum</ex> quos B<ex>er</ex>nard<ex>us</ex> d<ex>omi</ex>n<ex>u</ex>s de Benevento p<ex>er</ex>cipere co<ex>n</ex>suev<ex>er</ex>at annuatim seu annis singulis in et super emolumentis pedagii de Larsis terre de Petra et qui septuaginta sol<ex>idi</ex> ad solvend<ex>um</ex> retardati fu<ex>er</ex>ant d<ex>i</ex>c<ex>t</ex>o d<ex>omi</ex>no de Benevento p<ex>er</ex> sex annos tempore q<ex>u</ex>o m<ex>ih</ex>i p<ex>re</ex>ceptum fuit d<ex>e</ex> solvendis d<ex>i</ex>c<ex>t</ex>or<ex>um</ex> sex annor<ex>um</ex> ar<ex>re</ex>ragiis suprad<ex>i</ex>c<ex>t</ex>Is. <br/> Except<ex>i</ex>oni d<ex>i</ex>c<ex>t</ex>e peccunie p<ex>er</ex> vos m<ex>ih</ex>i no<ex>n</ex> defalcate renu<ex>n</ex>cio. <br/> Act<ex>um</ex> in domo thesaurarie, p<ex>re</ex>sentib<ex>us</ex> testib<ex>us</ex> mag<ex>ist</ex>ro Helie Bernardi, Johanne Manni, R<ex>aimund</ex>ino Petri de Nem<ex>auso</ex>, et me Paulo Andree, pu<ex>bli</ex>co d<ex>i</ex>c<ex>t</ex>i d<ex>omi</ex>ni regis notario, q<ex>u</ex>i p<ex>re</ex>d<ex>i</ex>c<ex>t</ex>is presens fui et predicta in notam recepi et requisit<ex>us</