INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (36,36,'jpilla','Conoguda chausa sia q<ex>ue</ex> p<ex>er</ex> II tortas de ceu redens ad la S. Marsal e p<ex>er</ex> VI denairadas de charn red<ex>ens</ex> ad la veilla de Pasqes q<ex>ue</ex> J. Arnaus, lo mazelliers, lo frair Math. Arnau, devia ad W. de Villaivenc, au fil P. de Villai- -venc, de son banx qui es jostau banx Guio Boti, fuz chauza adcordada per ambas las partidas que J. Arnaus, o sil qui seran tenedor d’aqest banx, l’en deu redre chasqe an VIex sl. e VIex d. red<ex>ens</ex> ad W. de Villaivenc o au sos, la meitat ad la S. Johan e la meitat ad Nadal, per las II tortas e per las VI denairadas d<ex>e</ex> la charn red<ex>ens</ex> de sus nu<ex>m</ex>pnadas q<ex>ue</ex> remanen quittas ad J. Ar- -nau e au sos p<ex>er</ex> los VI sl. e VI den. red<ex>ens</ex>, q<ex>ue</ex> W. de Villaivenc pres en aqest banx J. Arnau si c<ex>um</ex> es numpnaz de suz. Dat<ex>um</ex> ad petit<ex>io</ex>n<ex>em</ex> p<ex>ar</ex>tiu<ex>m</ex>, d<ex>i</ex>c<ex>t</ex>o W. investito, mense jan<ex>uarii</ex> a<ex>n</ex>no gr<ex>ati</ex>e M° CC° XL° q<ex>ui</ex>nto.');
INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (37,37,'jpilla','Ego Clarembald<ex>us</ex> miles, d<ex>omi</ex>n<ex>u</ex>s Vendolii, notum facio omnibus tam p<ex>re</ex>sentibus q<ex>u</ex>am fut<ex>ur</ex>is q<ex>uo</ex>d ego decem <ex>et</ex> septem libratas <ex>et</ex> dimidiam t<ex>er</ex>re annuas parisien<ex>sis</ex> monete, sitas apud Pouilliacum <ex>et</ex> in territorio de Pouilliaco in caponibus et terris arabilib<ex>us</ex>, q<ex>u</ex>as redemi a Bartholomeo Cato, burgen<ex>si</ex> Peronen<ex>si</ex>, q<ex>u</ex>as ip<ex>s</ex>e Bartholome<ex>us</ex> acq<ex>u</ex>isiverat de me <ex>et</ex> fratre meo Guidone, tradidi <ex>et</ex> concessi eidem Guidoni fratri meo in partem terre pro reconpensatione excantie ipsum Guidonem ex parte patris et matris ejus contingentis ; <ex>et</ex> Guido d<ex>i</ex>c<ex>t</ex>as decem <ex>et</ex> septem libratas <ex>et</ex> dimidiam t<ex>er</ex>re <ex>et</ex> ejus h<ex>er</ex>edes tenebunt de me <ex>et</ex> de meis h<ex>er</ex>edib<ex>us</ex> cum illo feodo q<ex>uo</ex>d idem Guido alibi de me tenet ad unum feodum conjunctum <ex>et</ex> unitum <ex>et</ex> ad unu<ex>m</ex> solum homagium, unum inde s<ex>er</ex>vicium reddituri p<ex>ro</ex>ut p<ex>r</ex>imum feodum m<ex>ih</ex>i debet, <ex>et</ex> ego teneor eidem Guidoni <ex>et</ex> ejus h<ex>er</ex>edibus garandire de me <ex>et</ex> meis h<ex>er</ex>edib<ex>us</ex> totum ill<ex>u</ex>d feodum <ex>et</ex> adv<ex>er</ex>sus omnes homines q<ex>u</ex>i ad jus venire  volu<ex>er</ex>int <ex>et</ex> ad legem tanq<ex>u</ex>am d<ex>omi</ex>n<ex>us</ex> ad usus <ex>et</ex> consuetudines patrie in q<ex>u</ex>a feodum hoc est situm, salva tamen michi vel meis h<ex>er</ex>edibus legitima excantia si even<ex>er</ex>it de feodo sup<ex>r</ex>adicto. Ut autem h<ex>ec</ex> omnia facta <ex>et</ex> ordinata p<ex>er</ex> meu<ex>m</ex> assensum <ex>et</ex> p<ex>er</ex> meam voluntatem coram meis hominib<ex>us</ex>, parib<ex>us</ex> ip<ex>s</ex>ius Guidonis, ad hoc ev