INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (45,45,'jpilla','Innocentius ep<ex>iscopu</ex>s, servus servor<ex>um</ex> Dei, k<ex>arissi</ex>mo in Chr<ex>ist</ex>o filio . . regi Francie illustri, sal<ex>u</ex>t<ex>em</ex> et ap<ex>osto</ex>licam ben<ex>edictionem</ex>. Propter veritatem, mansuetudinem et justitiam divinitus inspiratas que te pre regibus terre fecerunt excelsum, plenam de te in D<ex>omi</ex>no fiduciam obtinentes, magnificentiam tuam ad ea que ministerium regie sublimitatis respiciunt precibus et monitis eo libentius et co<ex>n</ex>fidentius invitam<ex>us</ex> quo te carius amplexantes in visceribus Je<ex>s</ex>u Chr<ex>ist</ex>i gr<ex>ati</ex>am ap<ex>u</ex>d Deum et laudem ap<ex>u</ex>d homi<ex>n</ex>es exin<ex>de</ex> propensius te cupim<ex>us</ex> promereri. <br/> Cum igit<ex>ur</ex>, sicut accepim<ex>us</ex>, nobilis vir . . senescalcus Belliquadri, balivus tuus, dilectos filios Joh<ex>ann</ex>em de Sangenes, Lanfrancum Spinolum, Simonem de S[ta<ex>n</ex>t]ion et Joh<ex>ann</ex>em Dalmatii, cives Januen<ex>ses</ex>, ad Latiniacen<ex>ses</ex> nundinas cum suis mercimoniis accedentes eisdem mercimoniis spoliarit, <br/> celsitudinem regiam rogandam duximus attenti<ex>us</ex> et hortandam q<ex>u</ex>atin<ex>us</ex> predictis civibus mercimonia ip<ex>s</ex>a pro ap<ex>osto</ex>lice Sedis et n<ex>ost</ex>ra reverentia restitui facias universa, preces n<ex>ost</ex>ras in hac parte taliter impleturus q<ex>uo</ex>d iidem eas sibi sentiant profuisse et nos excellentiam tuam proinde valeam<ex>us</ex> no<ex>n</ex> i<ex>m</ex>merito co<ex>m</ex>me[n]dare. <br/> Dat<ex>um</ex> Lugdun<ex>i</ex>, XVIIII k<ex>a</ex>l<ex>endas</ex> febr<ex>uarii</ex>, pontificatus n<ex>ost</ex>ri anno sec<ex>un</ex>do. <br/> [Bulle : ] S<ex>ANCTUS</ex> PA<ex>ULUS</ex>, S<ex>ANCTUS</ex> PE<ex>TRUS</ex> / INNOCENTIUS P<ex>A</ex>P<ex>A</ex> IIII');
