INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (63,63,'jpilla','Anno ab Incarnatione Domini mill<ex>esi</ex>mo tricentesimo quarto, die XV mens<ex>is</ex> aprilis, indict<ex>i</ex>one s<ex>e</ex>c<ex>un</ex>da. Notum sit cunctis presentibus et futuris q<ex>uo</ex>d, cum reverendus in Chr<ex>ist</ex>o pater et d<ex>omi</ex>n<ex>u</ex>s Gaufridus, Dei gr<ex>ati</ex>a ep<ex>iscop</ex>us Vapincen<ex>sis</ex> mandasset p<ex>er</ex> suas litteras d<ex>omi</ex>no Odoni Baudoyni, rectori ecc<ex>lesi</ex>e S<ex>an</ex>c<ex>t</ex>i  Juliani de Bueychana q<ex>uo</ex>d certa die venire ad dictam ecc<ex>lesi</ex>am causa visitandi intendebat et pro dicta visitac<ex>i</ex>one, ut de jure est, recip<ex>er</ex>e procurac<ex>i</ex>onem, cumq<ex>ue</ex> religiosus vir frater Martinus, prior monasterii de Dorbono et ide<ex>m</ex> rector ecc<ex>lesi</ex>e s<ex>u</ex>p<ex>r</ex>adicte contenderent de procurac<ex>i</ex>one ip<ex>s</ex>i d<ex>omi</ex>no ep<ex>iscop</ex>o facienda et dicerent unus alterum teneri ad dictam procurac<ex>i</ex>onem faciendam et sup<ex>er</ex> hoc idem d<ex>omi</ex>n<ex>u</ex>s ep<ex>iscop</ex>us ad requisic<ex>i</ex>onem d<ex>i</ex>c<ex>t</ex>i rectoris scripsisset p<ex>re</ex>fato priori de Dorbono ut dictam procurac<ex>i</ex>onem rac<ex>i</ex>one visitac<ex>i</ex>onis d<ex>i</ex>c<ex>t</ex>e ecc<ex>lesi</ex>e eidem d<ex>omi</ex>no ep<ex>iscop</ex>o pro hiis que dom<ex>us</ex> Dorboni de redditibus et proventibus ecc<ex>lesi</ex>e S<ex>an</ex>c<ex>t</ex>i Juliani p<ex>er</ex>cipiebat in d<ex>i</ex>c<ex>t</ex>a domo Durboni fac<ex>er</ex>et ut alias sibi d<ex>omi</ex>no ep<ex>iscop</ex>o et predecessoribus suis erat fieri consuetum, veniens postq<ex>u</ex>a<ex>m</ex> sibi scripsit idem d<ex>omi</ex>n<ex>u</ex>s prior ad presenciam d<ex>i</ex>c<ex>t</ex>i d<ex>omi</ex>ni ep<ex>iscop</ex>i excusando se dixit et p<ex>ro</ex>posuit coram eo q<ex>uo</ex>d no<ex>n</ex> tenebatur fac<ex>er</ex>e dictam p<ex>ro</ex>curac<ex>i</ex>onem et q