INSERT INTO transcription (transcription_id,doc_id,user_ref,content) VALUES (2801,28,'jpilla','Anno ab incarnatione DUc, coms de Rodes, <ex>et</ex> eu, <ex>Guillem</ex>, coms de Rodes, sosBonacumba Bertran l’abat Panat queCambolaz, BPebrac e fraire Isarnz.Uc, comte de Rodez, et moi, Guilhem, comte de Rodez, sonBonnecombe, à Bertrand l’abbé et auxPanat quiCamboulas, Bernart Beringuer de Panat,Pébrac e frère Isarnz.<ex>omi</ex>ni M° CC° III°. Eu, <br/>fills, p<ex>er</ex> nos e per totz los n<ex>ost</ex>res, p<ex>er</ex> bona fe e ses engan, <ex>et</ex> ab aquesta carta, p<ex>er</ex> amor de Deu e de ma do<ex>n</ex>na s<ex>an</ex>c<ex>t</ex>a Maria e dellas armas de n<ex>ost</ex>res paires e de n<ex>ost</ex>ras maires e de tot n<ex>ost</ex>re linnatgue, donam e lauda<ex>m</ex> e liura<ex>m</ex> <ex>et</ex> ab titol p<ex>er</ex>feig de do nazo p<ex>er</ex> totz temps autorga<ex>m</ex>, so es saubut las maisos del borc de S<ex>anct</ex>i Amanz q<ex>ue</ex> foro d’en Guill<ex>em</ex> Echor e de Feiriol so fill, ab las cortz <ex>et</ex> ab l’ort <br/>p<ex>er</ex>tenementz que p<ex>er</ex>tener i devo, a Deu <ex>et</ex> a s<ex>an</ex>c<ex>t</ex>a Maria d’a <ex>et</ex> a.n <ex>et</ex> als fraires que ara i so ni p<ex>er</ex>adena<ex>n</ex>t i serau p<ex>er</ex> totz temps ; e desvestem ne nos e veste<ex>m</ex> ne la maiso. <ex>Et</ex> aquestas maisos sobredichas aiatz e tengatz francament e quitiament e soltas de comus e de gachas e de totz usatgues. <br/><br/>era cellarers de Bonacu<ex>m</ex>ba. <br/>S<ex>ignum</ex> Bec della Barreira, e W. della Barreira sos botz, B<ex>er</ex>nart Gausb<ex>er</ex>t, W. B<ex>er</ex>inguer de <ex>er</ex>nart B<ex>er</ex>inguer de Panat, D. Mala Mosca, Espero, R. Cordura, P. Gros, Corbeira. D. Socada, Ponz de <br/>Tot aquest dos sobredigz, eu Uc, coms de Rodes <ex>et</ex> eu Guillem, sos fills, coms de Rodes, volem q<ex>ue</ex> sia tengutz p<ex>er</ex> totz temps p<ex>er</ex> nos e p<ex>er</ex> totz n<ex>ost</ex>res successors e p<ex>er</ex> 